INSERT INTO `posts` (`p_id`, `p_title`, `p_content`, `p_author`, `p_view_count`, `p_comment_count`, `p_excerpt`, `p_catalogue`, `p_status`, `p_published`, `p_guid`, `p_comment_allow`, `p_menu_order`, `p_type`, `p_banner`, `p_password`, `p_parent`, `p_youtube_url`, `p_cue`) VALUES
(2, 'Rap về phim hoạt hình Doremon', '<div class="video-responsive" style="text-align: center;"><iframe src="https://www.youtube.com/embed/BLX0PNx66Fc" width="560" height="315" frameborder="0"></iframe></div>\r\n<div style="text-align: center;">&nbsp;</div>\r\n<div style="text-align: center;">Xin ch&agrave;o m&igrave;nh ch&iacute;nh l&agrave; Doremon, ch&uacute; m&egrave;o m&aacute;y đến từ tương lai.<br /> V&agrave; đ&acirc;y ch&iacute;nh l&agrave; Nobita khỏi n&oacute;i cũng biết cậu đang ngủ say<br /> V&igrave; t&iacute;nh lười biếng cộng th&ecirc;m ham chơi đ&ocirc;i khi cậu qu&ecirc;n lu&ocirc;n việc học<br /> Thường bị Jaien v&agrave; Xuneo chế nhạo cũng v&igrave; cậu hay kh&oacute;c<br /> Cậu thường sang nh&agrave; c&ocirc; b&eacute; Xuka v&igrave; cậu rất th&iacute;ch c&acirc;u b&eacute; n&agrave;y<br /> Cậu muốn sau n&agrave;y được cưới c&ocirc; ấy đ&oacute; l&agrave; l&yacute; do tớ đến đ&acirc;y<br /> Cũng v&igrave; bản chất cậu vốn đ&atilde; lười đ&ocirc;i khi mẹ cậu hay la mắng<br /> Những khi Jaien rủ chơi b&oacute;ng ch&agrave;y tớ biết cậu ta rất lo lắng<br /> Cậu thường đến trường rất muộn v&agrave; đ&oacute; như l&agrave; một th&oacute;i quen<br /> Thương k&ecirc;u m&igrave;nh cho mượn c&aacute;c bảo bối để đạt được th&agrave;nh c&ocirc;ng trong hổ thẹn<br /> Nhưng rồi một ng&agrave;y cậu chợt nhận ra nếu cứ dựa v&agrave;o c&aacute;c bảo bối ấy<br /> Th&igrave; cậu vẫn m&atilde;i l&agrave; thằng v&ocirc; dụng cả trong hiện tại lẫn cả tương lai<br /> Cậu quyết t&acirc;m học h&agrave;nh chăm chỉ d&ugrave; thất bại cũng rất nhiều lần<br /> Đ&ocirc;i l&uacute;c ng&atilde; quỵ trong sự cố gắng nhưng chưa một lần cậu muốn dừng ch&acirc;n<br /> Cậu muốn m&igrave;nh sẽ học thật giỏi để chiến thắng Dekisugi<br /> Một người bạn trong rất ho&agrave;n thiện lu&ocirc;n vượt mặt cậu trong những k&igrave; thi<br /> M&igrave;nh cho cậu ta nh&igrave;n thấy tương lai tiếp th&ecirc;m động lực cho cậu cố gắng<br /> Cho cậu ta biết người vợ tương lai cậu ch&iacute;nh l&agrave; c&ocirc; b&eacute; Shizuka<br /> Cậu rất vui khi biết điều đ&oacute; nhưng kh&ocirc;ng &iacute;t lần bị hố đ&acirc;u nha<br /> Đ&oacute; l&agrave; những lần nh&igrave;n thấy anh ch&agrave;ng Deki ngồi cạnh c&ocirc; n&agrave;ng Xuka<br /> <br /> <img class="wp-image-230" src="../../assets/images/daftar-kartun-favorit.jpg" alt="Rap về phim hoạt h&igrave;nh doremon" /></div>\r\n<div style="text-align: center;"><br /> H&igrave;! C&ograve;n đ&acirc;y l&agrave; những suy nghĩ của tớ về cậu ấy.... trong khoảng thời gian đ&oacute;..<br /> Bạn đ&uacute;ng l&agrave; một đứa vụng về l&agrave;m điều g&igrave; cũng thật chậm chạp<br /> Học th&igrave; cũng chẳng đ&acirc;u ra đ&acirc;u lại c&ograve;n lười biếng lại hay hấp tấp<br /> L&uacute;c c&oacute; chuyện th&igrave; dễ bỏ cuộc, chơi thể thao th&igrave; cũng kh&ocirc;ng xong<br /> T&iacute;nh t&igrave;nh nh&uacute;t nh&aacute;t lại c&ograve;n lơ đển, cả khi đi đứng cậu c&ograve;n kh&ocirc;ng xong<br /> Cậu l&uacute;c n&agrave;o cũng g&acirc;y phiền phức với c&aacute;i t&iacute;nh qu&ecirc;n trước qu&ecirc;n sau<br /> Ai n&oacute;i l&agrave;m sao th&igrave; cậu nghe vậy kh&ocirc;ng đ&ograve;i hỏi đầu đu&ocirc;i n&oacute; như thế n&agrave;o<br /> Hờ một ch&uacute;t l&agrave; nhờ m&igrave;nh gi&uacute;p kh&ocirc;ng tự l&agrave;m được g&igrave; cả<br /> L&uacute;c n&agrave;o cậu cũng l&agrave; người đ&ograve;i hỏi, nh&igrave;n cậu đ&ocirc;i l&uacute;c tớ cũng thấy x&oacute;t xa<br /> M&igrave;nh đ&atilde; lo sẽ c&oacute; một ng&agrave;y, m&igrave;nh sẽ phải quay về với tương lai<br /> V&igrave; khi nhiệm vụ được giao ho&agrave;n tất, d&ugrave; cho m&igrave;nh c&oacute; muốn cũng chẳng thể ở lại<br /> V&agrave; rồi cuối c&ugrave;ng mọi chuyện cũng đến, tạm biệt cậu tớ phải đi đ&acirc;y<br /> Chỉ mong sao thời gian ngừng tr&ocirc;i để tớ được thấy cậu m&atilde;i như vậy<br /> <br /> Sau đ&oacute; tớ trở về tương lai...<br /> D&ugrave; l&agrave; kh&ocirc;ng gặp nhau rất l&acirc;u! nhưng tớ vẫn nhớ cậu ấy...<br /> Tớ mong được gặp cậu ấy th&ecirc;m một lần nữa<br /> <br /> V&agrave;i năm sau đ&oacute; m&igrave;nh kh&ocirc;ng gặp nhau v&agrave; rồi điều ước đ&atilde; th&agrave;nh hiện thực<br /> Nhờ lời n&oacute;i dối m&agrave; cậu thốt ra trong l&uacute;c cậu đang n&oacute;i chuyện luy&ecirc;n thuy&ecirc;n<br /> V&agrave; bảo bối đ&atilde; c&oacute; t&aacute;c dụng, tớ được quay trở lại nơi đ&acirc;y<br /> Gi&uacute;p Nobita vượt qua kh&oacute; khăn c&ugrave;ng nhau bước tiếp những h&agrave;nh tr&igrave;nh d&agrave;i.</div>', 1, 103, 0, 'Bài rap rất hay về phim hoạt hình nổi tiếng đến từ Sứ sở hoa Anh đào mang tên Doremon', NULL, 'public', '2016-08-11 12:31:37', 'rap-ve-phim-hoat-hinh-doremon', 1, 0, 'post', 'daftar-kartun-favorit1.jpg', '', 0, '', ''),
(3, 'Trang chủ', '', 0, 0, 0, '', '', 'public', '2016-07-25 17:23:06', 'http://btit95.esy.es/', 0, 0, 'navigation', '', '', 0, '', ''),
(4, 'Liên hệ', 'Mọi &yacute; kiến xin vui l&ograve;ng gửi về email <strong>BaoToan.95@gmail.com</strong>&nbsp;hoặc điền v&agrave;o biểu mẫu b&ecirc;n dưới:', 1, 0, 0, '', NULL, 'public', '2016-07-25 17:25:35', 'lien-he', 1, 0, 'page', '13439007_552355538305301_342366450246934188_n13.jpg', '', 0, '', ''),
(5, 'Chọn ngẫu nhiên từ cơ sở dữ liệu', 'Trong một số trường hợp, ch&uacute;ng ta muốn chọn ra ngẫu nhi&ecirc;n một số d&ograve;ng từ một hay nhiều bảng trong database như chọn ra random người d&ugrave;ng tr&uacute;ng giải, chọn random giải thưởng,... C&oacute; rất nhiều c&aacute;ch để l&agrave;m việc n&agrave;y, tuy nhi&ecirc;n kh&ocirc;ng phải c&aacute;ch n&agrave;o cũng tốt, việc truy vấn csdl c&oacute; thể tốn rất nhiều t&agrave;i nguy&ecirc;n v&agrave; thời gian nếu kh&ocirc;ng biết c&aacute;ch tối ưu n&oacute;.<br />\r\n<br />\r\n<strong>L&agrave;m thế n&agrave;o để chọn ngẫu nhi&ecirc;n từ csdl?</strong><br />\r\n&Yacute; tưởng đơn giản nhất c&oacute; thể l&agrave; select to&agrave;n bộ dữ liệu của một table hoặc một tập table n&agrave;o đ&oacute; sau đ&oacute; order by RAND() để sắp xếp random thứ tự của result, cuối c&ugrave;ng d&ugrave;ng LIMIT để lấy số d&ograve;ng ch&uacute;ng ta cần.<br />\r\nC&aacute;ch n&agrave;y kh&aacute; đơn giản v&agrave; cũng kh&aacute; ph&ugrave; hợp với những table c&oacute; lượng dữ liệu nhỏ, thử nghĩ nếu dữ liệu l&ecirc;n tới h&agrave;ng ng&agrave;n row nhưng ta chỉ muốn lấy random 100 rows trong số đ&oacute; th&igrave; việc get to&agrave;n bộ dữ liệu sau đ&oacute; sắp xếp random n&oacute; tốn bao nhi&ecirc;u thời gian? V&agrave; chưa kể c&aacute;ch tr&ecirc;n chỉ &aacute;p dụng với những table c&oacute; primary key l&agrave; kiểu int (integer) v&agrave; tăng li&ecirc;n tục (nếu kh&ocirc;ng tăng li&ecirc;n tục th&igrave; khi random v&agrave;o một id n&agrave;o đ&oacute; m&agrave; id đ&oacute; kh&ocirc;ng tồn tại trong table th&igrave; kết quả sẽ kh&ocirc;ng ổn).<br />\r\n<br />\r\n<strong>Như thế n&agrave;o mới được gọi l&agrave; nhanh?</strong><br />\r\nViệc select to&agrave;n bộ table để lấy random một hoặc nhiều d&ograve;ng l&agrave; chậm, vậy th&igrave; ta c&oacute; thể suy ra được như thế n&agrave;o l&agrave; nhanh rồi. Chỉ c&oacute; thể l&agrave; kh&ocirc;ng select to&agrave;n bộ m&agrave; chỉ select random th&ocirc;i! Sau đ&acirc;y l&agrave; c&aacute;c kĩ thuật fetching random cho cả trường hợp primary key kiểu int v&agrave; một số kiểu kh&aacute;c:<br />\r\n<br />\r\n<strong>Trường hợp 1: Cho table c&oacute; id l&agrave; <u><em>Auto Crease</em></u>&nbsp;li&ecirc;n tục tăng 1 đơn vị, trả về 1 row</strong><br />\r\n<u>&Yacute; tưởng</u>: Ở đ&acirc;y ch&uacute;ng ta kh&ocirc;ng get to&agrave;n bộ data trong table nữa m&agrave; chỉ get max v&agrave; min chỉ số id rồi &aacute;p dụng theo c&aacute;ch đơn giản tr&ecirc;n.<br />\r\n<u>Code v&iacute; dụ</u>:\r\n<pre>\r\n<a name="case_consecutive_auto_increment_without_gaps_1_row_returned"> SELECT r.*\r\n        FROM (\r\n            SELECT FLOOR(mm.min_id + (mm.max_id - mm.min_id + 1) * RAND()) AS id\r\n                FROM (\r\n                    SELECT MIN(id) AS min_id,\r\n                           MAX(id) AS max_id\r\n                        FROM RandTest\r\n                     ) AS mm\r\n             ) AS init\r\n        JOIN  RandTest AS r  ON r.id = init.id;</a></pre>\r\n', 1, 47, 0, 'Trong một số trường hợp, ch&uacute;ng ta muốn chọn ra ngẫu nhi&ecirc;n một số d&ograve;ng từ một hay nhiều bảng trong database v&agrave; c&oacute; rất nhiều c&aacute;ch để l&agrave;m việc n&agrave;y, tuy nhi&ecirc;n kh&ocirc;ng phải c&aacute;ch n&agrave;o cũng tốt, việc truy vấn csdl c&oacute; thể tốn rất nhiều t&agrave;i nguy&ecirc;n nếu kh&ocirc;ng biết c&aacute;ch tối ưu n&oacute;.', NULL, 'public', '2016-07-26 10:15:57', 'chon-ngau-nhien-tu-co-so-du-lieu', 1, 0, 'post', 'sql.jpg', 'baotoan123', 0, '', ''),
(6, 'Tonight you belong to me', '<div style="text-align: center;"><iframe frameborder="0" height="315" src="https://www.youtube.com/embed/Bpu0TIXzI1w" width="560"></iframe></div>\r\n\r\n<div><br />\r\n<strong>Lời b&agrave;i h&aacute;t:</strong><br />\r\n<br />\r\nI know you belong to somebody new<br />\r\nBut tonight you belong to me<br />\r\n<br />\r\nAlthought we&#39;re apart&nbsp;<br />\r\nYou are a part of my heart<br />\r\nAnd tonight you belong to me<br />\r\n<br />\r\nWait down by stream<br />\r\nHow sweet it will seem<br />\r\nOnce more just to dream<br />\r\nIn the moonlight<br />\r\nMy honey I know<br />\r\nWith the dawn<br />\r\nThay you, will be gone<br />\r\nBut tonight, you belong to me<br />\r\n<br />\r\nWait down by the stream<br />\r\nHow sweet it will seem<br />\r\nOnce more just to dream<br />\r\nIn the moonlight<br />\r\nMy honey I know<br />\r\nWith the dawn<br />\r\nThay you, will be gone<br />\r\nBut tonight, you belong to me<br />\r\nJust a little old me</div>\r\n', 1, 97, 1, 'B&agrave;i h&aacute;t Tonight you belong to me phi&ecirc;n bản hai bố con cực dễ thương', NULL, 'public', '2016-07-26 10:28:18', 'tonight-you-belong-to-me', 1, 0, 'post', 'Untitled.png', '', 0, '', ''),
(8, 'PHP Designer 8.1.2 Full key và cách cài đặt', '<p><strong>PHP Designer</strong>&nbsp;l&agrave; c&ocirc;ng cụ d&ugrave;ng để code PHP chuy&ecirc;n nghiệp, kh&aacute; nhiều lập tr&igrave;nh vi&ecirc;n ưa chuộng ở VN cũng như nước ngo&agrave;i.<br /> N&oacute; cung cấp một số t&iacute;nh năng như:</p>\r\n<ul>\r\n<li>Soạn thảo, chỉnh sửa code</li>\r\n<li>Tăng tốc độ soạn thảo bằng việc nhắc code</li>\r\n<li>Hỗ trợ c&aacute;c framework như Zend, CodeIgniter, Yii,...</li>\r\n<li>Hỗ trợ c&aacute;c framework&nbsp;Javascript như&nbsp;jQuery, YUI,...</li>\r\n<li>Đa ng&ocirc;n ngữ với hơn 200 lựa chọn</li>\r\n</ul>\r\n<p><strong>Link download PHP Designer 8.1.2 bao gồm key tại <em><a href="http://www.mediafire.com/download/9gubpsl9d4rxb73/PHP+designer+full+key.rar"><span style="color: #ff0000;">đ&acirc;y</span></a></em></strong><br /> <br /> <strong>C&aacute;ch c&agrave;i đặt:</strong></p>\r\n<ul>\r\n<li>Bước 1: Download v&agrave; giải n&eacute;n</li>\r\n<li>Bước 2: Chạy file&nbsp;phpdesigner_8_1_2_setup.exe nhấn next v&agrave; finish</li>\r\n<li>Bước 3: Khởi động phần mềm đ&atilde; c&agrave;i đặt</li>\r\n<li>Bước 4: Bước 4 v&agrave;o Help -&gt; registration...</li>\r\n</ul>\r\n<div style="text-align: center;"><img class="lazy zoomable" style="height: 189px; width: 500px;" alt="" data-original="../../assets/images/b1.png" /><br /> &nbsp;</div>\r\n<ul>\r\n<li>Bước 5: Nhập th&ocirc;ng tin trong file&nbsp;serial PHPDesigner 8.1.2.txt</li>\r\n</ul>\r\n<div style="text-align: center;"><img class="lazy zoomable" style="height: 419px; width: 500px;" alt="" data-original="../../assets/images/b2.png" /><br /> &nbsp;</div>\r\n<ul>\r\n<li>Bước 6: Nhấn Register License l&agrave; xong</li>\r\n</ul>\r\n<div style="text-align: center;"><img class="lazy zoomable" style="height: 418px; width: 500px;" alt="" data-original="../../assets/images/b3.png" /></div>\r\n<div><br /> Ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng!!!</div>', 1, 26, 0, 'Chia sẻ PHP Designer 8.1.2 full key<br />\r\nĐây là công cụ dùng để code PHP chuyên nghiệp, khá nhiều lập trình viên ưa chuộng ở VN cũng như nước ngoài.', NULL, 'public', '2016-07-30 04:13:45', 'php-designer-8-1-2-full-key-va-cach-cai-dat', 1, 0, 'post', 'Capture1.PNG', '', 0, '', ''),
(9, 'Tạo facebook application', '<p>Để tương t&aacute;c với Facebook việc l&agrave;m đầu ti&ecirc;n ch&uacute;ng ta cần l&agrave;m l&agrave; tạo một app facebook. Trong app n&agrave;y, facebook hỗ trợ rất nhiều t&iacute;nh năng hữu &iacute;ch như lấy th&ocirc;ng tin người d&ugrave;ng, thực hiện chức năng login cho trang web th&ocirc;ng qua facebook,....</p>\r\n<p>Ch&uacute;ng ta sẽ tiến h&agrave;nh c&aacute;c bước tạo app facebook cơ bản:&nbsp;</p>\r\n<ul>\r\n<li>Đăng nhập v&agrave;o facebook</li>\r\n<li>Truy cập v&agrave;o địa chỉ:&nbsp;https://developers.facebook.com/</li>\r\n</ul>\r\n<div style="text-align: center;"><img class="lazy zoomable" style="width: 500px; height: 281px;" alt="tạo app facebook" data-original="/assets/images/devfb.png" /></div>\r\n<div style="text-align: center;">&nbsp;</div>\r\n<ul>\r\n<li>Tạo một app mới v&agrave;o My apps -&gt; Add a new app</li>\r\n</ul>\r\n<div style="text-align: center;"><img class="lazy zoomable" style="width: 500px; height: 281px;" alt="tạo app facebook" data-original="/assets/images/newapp.png" /></div>\r\n<div style="text-align: center;">&nbsp;</div>\r\n<ul>\r\n<li>Chọn platform l&agrave; Website</li>\r\n</ul>\r\n<div style="text-align: center;"><img class="lazy zoomable" style="width: 500px; height: 281px;" alt="chọn platform app facebook" data-original="/assets/images/platform.png" /></div>\r\n<div style="text-align: center;">&nbsp;</div>\r\n<ul>\r\n<li>Nhập t&ecirc;n app v&agrave; nhấn Create New Facebook App ID</li>\r\n</ul>\r\n<div style="text-align: center;"><img class="lazy zoomable" style="width: 500px; height: 281px;" alt="đặt t&ecirc;n cho app facebook" data-original="/assets/images/createid.png" /></div>\r\n<div style="text-align: center;">&nbsp;</div>\r\n<ul>\r\n<li>Nhập Contact name l&agrave; email của bạn v&agrave; chọn Category ph&ugrave; hợp rồi nhấn Create App ID.</li>\r\n</ul>\r\n<div style="text-align: center;"><img class="lazy zoomable" style="width: 500px; height: 281px;" alt="hướng dẫn tạo app facebook" data-original="/assets/images/cate.png" /></div>\r\n<div style="text-align: center;">&nbsp;</div>\r\n<ul>\r\n<li>Đến đ&acirc;y coi như bạn đ&atilde; tạo app th&agrave;nh c&ocirc;ng!</li>\r\n</ul>\r\n<div style="text-align: center;"><img class="lazy zoomable" style="width: 500px; height: 281px;" alt="tạo app facebook th&agrave;nh c&ocirc;ng" data-original="/assets/images/ok.png" /><br /> &nbsp;</div>\r\n<p>Tiếp theo ch&uacute;ng sẽ tiến h&agrave;nh <strong><em><u><a href="../../setting-facebook-app-de-test-o-localhost-10.html">c&agrave;i đặt app để test ở localhost</a></u></em></strong></p>', 1, 73, 0, 'Hướng dẫn tạo facebook application với version mới nhất (v2.7)', NULL, 'public', '2016-07-29 12:02:29', 'tao-facebook-application', 1, 0, 'post', 'tao-facebook-application1469787908.jpg', '', 0, '', ''),
(11, 'Tạo chức năng login facebook đơn giản với javascript', '<p>Chức năng login th&ocirc;ng qua facebook kh&ocirc;ng c&ograve;n xa lạ với người d&ugrave;ng internet hiện nay, n&oacute; l&agrave;m giảm đ&aacute;ng kể thời gian đăng nhập của người d&ugrave;ng l&agrave;m cho website trở n&ecirc;n th&acirc;n thiện với người d&ugrave;ng hơn.</p>\r\n<p>Đ&acirc;y l&agrave; b&agrave;i viết tiếp theo của <a title="Setting facebook app de test o localhost" href="../../setting-facebook-app-de-test-o-localhost-10.html"><em><strong>Setting&nbsp;facebook app để test ở localhost&nbsp;</strong></em></a>, v&igrave; ch&uacute;ng ta sẽ test ở local n&ecirc;n nếu bạn chưa biết c&aacute;ch setting app th&igrave; vui l&ograve;ng tham khảo ở b&agrave;i trước, ch&uacute;ng ta sẽ lấy app đ&atilde; tạo ở những b&agrave;i trước để sử dụng cho b&agrave;i n&agrave;y.</p>\r\n<h5><strong>Kịch bản:</strong></h5>\r\n<p>Trong b&agrave;i viết n&agrave;y, ch&uacute;ng ta sẽ c&ugrave;ng&nbsp;tạo chức năng login n&agrave;y sử dụng javascript với kịch bản sau:<br />-&nbsp;Người d&ugrave;ng truy cập v&agrave;o website một button Login with Facebook sẽ được hiện ra.<br />- Nếu họ đ&atilde; đăng nhập v&agrave;o t&agrave;i khoản Facebook của họ v&agrave; nhấn v&agrave;o button login tr&ecirc;n website của ch&uacute;ng ta, một trang confirm của Facebook sẽ hiện ra để x&aacute;c nhận quyền lấy th&ocirc;ng tin người d&ugrave;ng th&ocirc;ng qua Facebook.<br />- Khi họ nhấn n&uacute;t confirm th&igrave; đăng nhập th&agrave;nh c&ocirc;ng, họ sẽ được chuyển lại website của ch&uacute;ng ta, l&uacute;c n&agrave;y sẽ ẩn button Login đi v&agrave; thay v&agrave;o đ&oacute; l&agrave; hiển thị th&ocirc;ng tin người d&ugrave;ng như t&ecirc;n v&agrave; địa chỉ của họ.</p>\r\n<h5>Thực hiện:</h5>\r\n<ul>\r\n<li>Bạn h&atilde;y v&agrave;o dashboard của facebook app đ&atilde; tạo, ch&uacute;ng ta sẽ lấy App ID</li>\r\n</ul>\r\n<p style="text-align: center;"><img class="lazy zoomable" alt="get app id" width="500" height="281" data-original="../../source/dashboard%20facebook.jpg?1469789605575" /></p>\r\n<ul>\r\n<li style="text-align: left;">Tiếp theo v&agrave;o Add Product b&ecirc;n sidebar v&agrave; nhấn v&agrave;o Get started trong mục&nbsp;Facebook Login</li>\r\n</ul>\r\n<p style="text-align: center;"><img class="lazy zoomable" alt="login facebook plugin" width="500" height="217" data-original="../../source/login%20facebook%20plugin.jpg?1469789820724" /></p>\r\n<ul>\r\n<li style="text-align: left;">K&eacute;o xuống dưới bạn h&atilde;y nhập url m&agrave; m&igrave;nh sẽ redirect tới sau khi người d&ugrave;ng đăng nhập th&agrave;nh c&ocirc;ng v&agrave;o khung Valid OAuth redirect URIs. Ở đ&acirc;y ch&uacute;ng ta sẽ redirect lại trang cũ tức l&agrave; trang index.html</li>\r\n</ul>\r\n<p style="text-align: center;"><img class="lazy zoomable" alt="facebook redirect url" width="500" height="234" data-original="../../source/link%20redirect.jpg?1469790236274" /></p>\r\n<ul>\r\n<li>Ok! Bạn h&atilde;y mở trang index.php trong thư mục fb l&ecirc;n v&agrave; edit nội dung như sau:</li>\r\n</ul>\r\n<pre class="language-markup"><code>&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n    &lt;meta charset="utf-8" /&gt;\r\n    &lt;title&gt;Demo Facebook Login Javascript&lt;/title&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n    &lt;script&gt;\r\n\r\n        // Init application\r\n        window.fbAsyncInit = function () {\r\n            FB.init({\r\n                appId: ''1567628656880346'', // Đổi App ID của bạn ở đ&acirc;y\r\n                cookie: true,\r\n                xfbml: true,\r\n                version: ''v2.5''\r\n            });\r\n            // Kiểm tra trạng th&aacute;i hiện tại\r\n            FB.getLoginStatus(function (response) {\r\n                statusChangeCallback(response);\r\n            });\r\n\r\n        };\r\n\r\n        // Xử l&yacute; trạng th&aacute;i đăng nhập\r\n        function statusChangeCallback(response) {\r\n            // Người d&ugrave;ng đ&atilde; đăng nhập FB v&agrave; đ&atilde; đăng nhập v&agrave;o ứng dụng\r\n            if (response.status === ''connected'') {\r\n                ShowWelcome();\r\n            }\r\n            // Người d&ugrave;ng đ&atilde; đăng nhập FB nhưng chưa đăng nhập ứng dụng\r\n            else if (response.status === ''not_authorized'') {\r\n                ShowLoginButton();\r\n            }\r\n            // Người d&ugrave;ng chưa đăng nhập FB\r\n            else {\r\n                ShowLoginButton();\r\n            }\r\n        }\r\n\r\n        // Gửi y&ecirc;u cầu đăng nhập tới FB\r\n        function RequestLoginFB() {\r\n            window.location = ''http://graph.facebook.com/oauth/authorize?'' \r\n+ ''client_id=1567628656880346&amp;scopes='' + // Đổi App ID của bạn ở đ&acirc;y\r\n''public_profile,email,user_likes&amp;redirect_uri=http://btit95.com/fb/index.html'';\r\n        }\r\n\r\n        // Hiển thị n&uacute;t đăng nhập\r\n        function ShowLoginButton() {\r\n            document.getElementById(''btb'').setAttribute(''style'', ''display:block'');\r\n            document.getElementById(''lbl'').setAttribute(''style'', ''display:none'');\r\n        }\r\n\r\n        // Ch&agrave;o mừng người d&ugrave;ng đ&atilde; đăng nhập\r\n        function ShowWelcome() {\r\n            document.getElementById(''btb'').setAttribute(''style'', ''display:none'');            \r\n            FB.api(''/me'', function (response) {\r\n                var name = response.name;\r\n                var id = response.id;\r\n                document.getElementById(''lbl'').innerHTML = \r\n				''&lt;h4&gt;You are logged with:&lt;/h4&gt;Name: '' + \r\nname + '' &lt;br/&gt;Facebook ID: '' + id;\r\n                document.getElementById(''lbl'').setAttribute(''style'', ''display:block'');\r\n            });\r\n        }\r\n\r\n    &lt;/script&gt;\r\n    &lt;div id="fb-root"&gt;&lt;/div&gt;\r\n    &lt;script&gt;\r\n    // Load facebook SDK\r\n    (function(d, s, id) {\r\n      var js, fjs = d.getElementsByTagName(s)[0];\r\n      if (d.getElementById(id)) return;\r\n      js = d.createElement(s); js.id = id;\r\n      js.src = "//connect.facebook.net/en_US/sdk.js#xfbml=1&amp;"\r\n + "version=v2.7&amp;appId=1567628656880346"; // Đổi App ID của bạn ở đ&acirc;y\r\n      fjs.parentNode.insertBefore(js, fjs);\r\n    }(document, ''script'', ''facebook-jssdk''));&lt;/script&gt;\r\n\r\n\r\n\r\n    &lt;!-- N&uacute;t đăng nhập --&gt;\r\n    &lt;input id="btb" type="button" \r\nvalue="Login with Facebook" onclick="RequestLoginFB()" style="display:none" /&gt;\r\n    &lt;p id="lbl" style="display:none"&gt;BẠN Đ&Atilde; ĐĂNG NHẬP TH&Agrave;NH C&Ocirc;NG!&lt;/p&gt;\r\n&lt;/body&gt;\r\n&lt;/html&gt;</code></pre>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li>B&acirc;y giờ bạn h&atilde;y truy cập v&agrave;o địa chỉ <a href="http://btit95.com/fb/">http://btit95.com/fb/</a>&nbsp;bạn sẽ được như sau</li>\r\n</ul>\r\n<p style="text-align: center;"><img class="lazy zoomable" alt="Login page" width="500" height="281" data-original="../../source/Login%20page.jpg?1469796232918" /></p>\r\n<ul>\r\n<li style="text-align: left;">M&igrave;nh chưa đăng nhập v&agrave;o facebook n&ecirc;n n&oacute; sẽ chuyển m&igrave;nh tới trang đăng nhập FB, m&igrave;nh tiến h&agrave;nh đăng nhập bằng t&agrave;i khoản FB của m&igrave;nh, nếu bạn đ&atilde; đăng nhập v&agrave;o FB rồi th&igrave; m&agrave;n h&igrave;nh n&agrave;y bạn h&atilde;y bỏ qua.</li>\r\n</ul>\r\n<p style="text-align: center;"><img class="lazy zoomable" alt="Login facebook" width="500" height="281" data-original="../../source/Login%20facebook.jpg?1469796369556" /></p>\r\n<ul>\r\n<li style="text-align: left;">Sau khi login FB th&agrave;nh c&ocirc;ng hoặc bạn đ&atilde; đăng nhập FB trước đ&oacute; th&igrave;&nbsp;sẽ được chuyển tới trang confirm quyền truy cập th&ocirc;ng tin cho app. Bạn h&atilde;y nhấn Okay để confirm.</li>\r\n</ul>\r\n<p style="text-align: center;"><img class="lazy zoomable" alt="Confirm access permission" width="500" height="281" data-original="../../source/Login%20fb%20success.jpg?1469796550263" /></p>\r\n<ul>\r\n<li style="text-align: left;">Tới đ&acirc;y FB sẽ đưa bạn trở lại site của m&igrave;nh, qu&aacute; tr&igrave;nh Login th&agrave;nh c&ocirc;ng. Ở&nbsp;đ&acirc;y ch&uacute;ng ta sẽ lấy được th&ocirc;ng tin của người d&ugrave;ng v&agrave; bạn cũng c&oacute; thể lưu th&ocirc;ng tin n&agrave;y xuống DB,...</li>\r\n</ul>\r\n<p style="text-align: center;"><img class="lazy zoomable" alt="Login facebook success" width="500" height="281" data-original="../../source/Login%20success.png?1469797103353" /></p>\r\n<p>Ch&uacute;c bạn th&agrave;nh c&ocirc;ng! Code tr&ecirc;n m&igrave;nh đ&atilde; giải th&iacute;ch kh&aacute; r&otilde;, v&igrave; b&agrave;i viết n&agrave;y m&igrave;nh mặc định bạn đ&atilde; biết về javascript, nếu kh&ocirc;ng c&aacute;c bạn c&oacute; thể tham khảo tr&ecirc;n google để hiểu hơn. Ngo&agrave;i ra, bạn c&oacute; thắc mắc g&igrave; vui l&ograve;ng comment b&ecirc;n dưới hoặc li&ecirc;n hệ với m&igrave;nh, m&igrave;nh sẽ cố gắng giải th&iacute;ch cho bạn.</p>', 1, 171, 3, 'Chức năng login thông qua facebook không còn xa lạ với người dùng internet hiện nay, nó làm giảm đáng kể thời gian đăng nhập của người dùng làm cho website trở nên thân thiện với người dùng hơn.', NULL, 'public', '2016-07-29 16:39:40', 'tao-chuc-nang-login-facebook-don-gian-voi-javascript', 1, 0, 'post', 'tao-chuc-nang-login-voi-facebook-don-gian-voi-javascript1469787866.jpg', '', 0, '', ''),
(10, 'Setting facebook app để test ở localhost', '<p>Đ&acirc;y l&agrave; b&agrave;i viết tiếp theo của <a title="Hướng dẫn tạo app facebook phi&ecirc;n bản mới nhất" href="../../tao-facebook-application-9.html"><span style="text-decoration: underline;"><em><strong>Tạo facebook application</strong></em></span>&nbsp;</a>nếu bạn chưa biết c&aacute;ch tạo app facebook th&igrave; c&oacute; thể thao khảo.<br /> <br /> L&agrave;m thế n&agrave;o để setting app facebook để c&oacute; thể test tr&ecirc;n localhost, đ&acirc;y l&agrave; một vấn đề kh&aacute; nan giải nếu lần đầu bạn tương t&aacute;c với facebook v&agrave; chưa c&oacute; một domain thật để test app của m&igrave;nh.<br /> <br /> Hiện nay facebook đ&atilde; kh&ocirc;ng c&ograve;n hỗ trợ việc config với domain localhost nữa n&ecirc;n ch&uacute;ng ta cần phải thay đổi domain name tr&ecirc;n m&aacute;y local. Trước ti&ecirc;n bạn phải c&agrave;i đặt một chương tr&igrave;nh tạo m&aacute;y chủ web, hiện nay c&oacute; kh&aacute; nhiều phần mềm giả lập như: Xampp, WampServer, VertrigoServ,... Bạn c&oacute; thể tham khảo tr&ecirc;n google để biết th&ecirc;m về vấn đề n&agrave;y, trong b&agrave;i n&agrave;y m&igrave;nh sẽ d&ugrave;ng Xampp v&agrave; m&igrave;nh&nbsp;mặc định l&agrave; bạn đ&atilde; c&agrave;i đặt v&agrave; khởi động chương tr&igrave;nh giả lập th&agrave;nh c&ocirc;ng.<br /> <br /> <span style="font-size: 14px;"><strong>Thay đổi domain name ở localhost</strong></span><br /> Khi ứng dụng giả lập server khởi động th&igrave; n&oacute; sẽ chạy tr&ecirc;n IP <strong><em>127.0.0.1</em></strong> v&agrave; thường c&oacute; domain name l&agrave; <em><strong>localhost</strong></em>&nbsp;để config lại domain bạn c&oacute; thể l&agrave;m theo c&aacute;c bước sau:</p>\r\n<ul>\r\n<li>Mở Notepad với quyền admin - chuột phải v&agrave;o Notepad v&agrave; chọn Run as administrator</li>\r\n<li>Trong notepad v&agrave;o File -&gt; Open chọn All file trong combobox&nbsp;v&agrave; mở file hosts trong&nbsp;C://Windows/System32/etc/</li>\r\n</ul>\r\n<p><img class="lazy zoomable" style="display: block; margin-left: auto; margin-right: auto;" alt="Cấu h&igrave;nh domain name" width="500" height="260" data-original="../../source/location.png?1469763060156" /></p>\r\n<ul>\r\n<li>Tiếp theo m&igrave;nh sẽ th&ecirc;m d&ograve;ng 127.0.0.1 btit95.com v&agrave;o cuối file, trong đ&oacute;:</li>\r\n</ul>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - &nbsp; 127.0.0.1 l&agrave; địa chỉ loopback&nbsp;hay n&oacute;i c&aacute;ch kh&aacute;c l&agrave; IP của localhost<br />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - &nbsp; btit95.com l&agrave; t&ecirc;n domain name m&igrave;nh muốn đặt cho địa chỉ IP của localhost</p>\r\n<p style="text-align: center;"><img class="lazy zoomable" style="display: block; margin-left: auto; margin-right: auto;" alt="sửa hosts file" width="500" height="258" data-original="../../source/edit%20hosts%20file.png?1469764071849" /></p>\r\n<ul>\r\n<li>Trong htdocs tạo một folder để test app m&igrave;nh đặt l&agrave; fb v&agrave; m&igrave;nh tạo một file index.html với nội dung sau&nbsp;trong folder n&agrave;y.</li>\r\n</ul>\r\n<pre class="language-markup"><code>&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n    &lt;meta charset="utf-8" /&gt;\r\n    &lt;title&gt;Demo Facebook Login Javascript&lt;/title&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n    &lt;h1&gt;Hello World&lt;/h1&gt;\r\n&lt;/body&gt;\r\n&lt;/html&gt;</code></pre>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><img class="lazy zoomable" alt="tạo folder test app" width="500" height="281" data-original="../../source/index.png?1469766071789" /></p>\r\n<ul>\r\n<li>Truy cập v&agrave;o địa chỉ btit95.com/fb/</li>\r\n</ul>\r\n<p style="text-align: center;"><img class="lazy zoomable" alt="tạo folder test th&agrave;nh c&ocirc;ng" width="500" height="281" data-original="../../source/Capture.png?1469766100696" /></p>\r\n<p style="text-align: left;">Nếu bạn nhận được kết quả tr&ecirc;n th&igrave; xin ch&uacute;c mừng, bạn đ&atilde; th&agrave;nh c&ocirc;ng!<br />C&aacute;c bạn c&oacute; thể thấy, b&acirc;y giờ ch&uacute;ng ta c&oacute; thể truy cập v&agrave;o btit95.com thay v&igrave; l&agrave; localhost như b&igrave;nh thường. Amazing....</p>\r\n<h5 style="text-align: left;"><strong>Cấu h&igrave;nh app facebook</strong></h5>\r\n<p style="text-align: left;">Tiếp theo ch&uacute;ng ta tiến h&agrave;nh setting app facebook. Bạn truy cập v&agrave;o dashboard app facebook bằng địa chỉ:&nbsp;<a href="https://developers.facebook.com/">https://developers.facebook.com/</a>&nbsp;chọn app của m&igrave;nh trong My Apps.</p>\r\n<p style="text-align: center;"><img class="lazy zoomable" alt="dashboard app facebook" width="500" height="281" data-original="../../source/dashboard%20facebook.png?1469767670411" /></p>\r\n<ul>\r\n<li style="text-align: left;">V&agrave;o mục setting v&agrave;o điền App domain l&agrave; t&ecirc;n domain m&igrave;nh mới config: btit95.com. Nhấn v&agrave;o Add platform chọn Web v&agrave; nhập v&agrave;o Site Url l&agrave; <a href="http://btit95.com/fb/">http://btit95.com/fb/</a>&nbsp;v&agrave; nhấn Save.</li>\r\n</ul>\r\n<p style="text-align: center;"><img class="lazy zoomable" alt="setting app domain" width="500" height="281" data-original="../../source/setting%20domain%20name.png?1469768345982" /></p>\r\n<ul>\r\n<li style="text-align: left;">Trong mục App Review c&oacute; phần&nbsp;Make [t&ecirc;n app của bạn]&nbsp;public? bạn nhấn v&agrave;o button ph&iacute;a dưới để public app của m&igrave;nh.</li>\r\n</ul>\r\n<p style="text-align: center;"><img class="lazy zoomable" alt="public app facebook" width="500" height="234" data-original="../../source/public%20facebook%20app.jpg?1469795168256" /></p>\r\n<p style="text-align: left;">Tới đ&acirc;y, bạn đ&atilde; setting app th&agrave;nh c&ocirc;ng trỏ về domain ở localhost. B&agrave;i tiếp theo ch&uacute;ng ta sẽ tiến h&agrave;nh x&acirc;y dựng Login facebook đơn giản.</p>', 1, 239, 0, 'Làm thế nào để setting app facebook để có thể test trên localhost, đây là một vấn đề khá nan giải nếu lần đầu bạn tương tác với facebook và chưa có một domain thật để test app của mình.', NULL, 'public', '2016-07-29 16:18:38', 'setting-facebook-app-de-test-o-localhost', 1, 0, 'post', 'setting-facebook-app-de-test-o-localhost1469722698.jpg', '', 0, '', ''),
(12, 'Tiếng Anh hằng ngày', '<p style="text-align: center;"><img class="lazy wp-caption" alt="BTIT95 learning English" width="500" height="497" data-original="../source/btit95%20learning%20english.jpg?1469798899467" /></p>\r\n<p style="text-align: left;"><em><strong><a title="Danh s&aacute;ch b&agrave;i học tiếng Anh" href="../../the-loai/tieng-anh">Danh s&aacute;ch tất cả b&agrave;i viết theo chủ đề tiếng Anh</a></strong></em></p>\r\n<p><strong>Danh s&aacute;ch c&aacute;c b&agrave;i luyện tiếng nghe tiếng Anh theo level:</strong><br /><em>Vẫn đang được cập nhật....</em></p>\r\n<p><strong>Level 1:</strong></p>\r\n<ol>\r\n<li><span style="text-decoration: underline;"><em><a title="Luyện tập tiếng Anh hằng ng&agrave;y" href="../../tieng-anh-hang-ngay-level-1-first-snow-fall-14.html">First snow fall</a></em></span></li>\r\n<li><span style="text-decoration: underline;"><em><a title="Jessica''s first day of school" href="../../tieng-anh-hang-ngay-level-1-lesson-2-jessica-s-first-day-of-school-19.html">Jessica''s first day of school</a></em></span></li>\r\n<li><span style="text-decoration: underline;"><em><a title="My flower garden" href="../../tieng-anh-hang-ngay-level-1-lesson-3-my-flower-garden-27.html">My flower garden</a></em></span></li>\r\n<li><em><span style="text-decoration: underline;"><a title="Going camping" href="../../tieng-anh-hang-ngay-level-1-lesson-4-going-camping-28.html">Going camping</a></span></em></li>\r\n<li><em><span style="text-decoration: underline;"><a title="My house" href="../../tieng-anh-hang-ngay-level-1-lesson-5-my-house-29.html">My house</a></span></em></li>\r\n<li><em><span style="text-decoration: underline;"><a title="Jennifer the firefighter" href="../../tieng-anh-hang-ngay-level-1-lesson-6-jennifer-the-firefighter-30.html">Jennifer the firefighter</a></span></em></li>\r\n<li><em><span style="text-decoration: underline;"><a title="Mark''s big game" href="../../tieng-anh-hang-ngay-level-1-lesson-7-mark-s-big-game-31.html">Mark''s big game</a></span></em></li>\r\n<li><em><span style="text-decoration: underline;"><a title="The easter egg hunt" href="../../tieng-anh-hang-ngay-level-1-lesson-8-the-easter-egg-hunt-32.html">The easter egg hunt</a></span></em></li>\r\n<li><em><span style="text-decoration: underline;"><a title="Summer vacation" href="../../tieng-anh-hang-ngay-level-1-lesson-9-summer-vacation-33.html">Summer vacation</a></span></em></li>\r\n<li><em><span style="text-decoration: underline;"><a title="Susan''s wedding day" href="../../tieng-anh-hang-ngay-level-1-lesson-10-susan-s-wedding-day-34.html">Susan''s wedding day</a></span></em></li>\r\n</ol>\r\n<p><strong>Level 2:</strong></p>\r\n<ol>\r\n<li><em><span style="text-decoration: underline;"><a title="Housework" href="../../tieng-anh-hang-ngay-level-2-lesson-1-housework-37.html">Housework</a></span></em></li>\r\n<li><em><span style="text-decoration: underline;"><a title="Weather" href="../../tieng-anh-hang-ngay-level-2-lesson-2-weather-38.html">Weather</a></span></em></li>\r\n<li><em><span style="text-decoration: underline;"><a title="School" href="../../tieng-anh-hang-ngay-level-2-lesson-3-school-39.html">School</a></span></em></li>\r\n<li><em><span style="text-decoration: underline;"><a title="Flowers" href="../../tieng-anh-hang-ngay-level-2-lesson-4-flowers-40.html">Flowers</a></span></em></li>\r\n<li><em><span style="text-decoration: underline;"><a title="Movies" href="../../tieng-anh-hang-ngay-level-2-lesson-5-movies-42.html">Movies</a></span></em></li>\r\n<li><em><span style="text-decoration: underline;"><a title="Travel" href="../../tieng-anh-hang-ngay-level-2-lesson-6-travel-41.html">Travel</a></span></em></li>\r\n<li><em><span style="text-decoration: underline;"><a title="Jobs" href="../../tieng-anh-hang-ngay-level-2-lesson-7-jobs-43.html">Jobs</a></span></em></li>\r\n<li><em><span style="text-decoration: underline;"><a title="Clothing" href="../../tieng-anh-hang-ngay-level-2-lesson-8-clothing-44.html">Clothing</a></span></em></li>\r\n<li>Wild animals</li>\r\n<li>Months</li>\r\n<li>Vegetables</li>\r\n<li>Spring</li>\r\n<li>Summer</li>\r\n<li>Autumn</li>\r\n<li>Winter</li>\r\n<li>Music</li>\r\n<li>Chirstmas</li>\r\n<li>Time</li>\r\n<li>Money</li>\r\n<li>Memories</li>\r\n<li>Making friends</li>\r\n<li>A baby</li>\r\n<li>Traffic</li>\r\n<li>The police</li>\r\n<li>New year''s day</li>\r\n<li>The future</li>\r\n<li>A ghost</li>\r\n<li>Television</li>\r\n<li>Me</li>\r\n<li>Hobbies</li>\r\n<li>The farm</li>\r\n<li>Food</li>\r\n<li>Parties</li>\r\n<li>Homework</li>\r\n<li>A surprise</li>\r\n<li>Pretending</li>\r\n<li>Emotions</li>\r\n<li>The doctor</li>\r\n<li>Pets</li>\r\n<li>The lie</li>\r\n</ol>\r\n<p><strong>Level 3:</strong></p>\r\n<ol>\r\n<li>Super heroes</li>\r\n<li>Home alone</li>\r\n<li>University</li>\r\n<li>Joking</li>\r\n<li>Halloween</li>\r\n<li>Moving to another country</li>\r\n<li>My first job</li>\r\n<li>Advice</li>\r\n<li>Health</li>\r\n<li>The musician</li>\r\n<li>The circus</li>\r\n<li>Day dream</li>\r\n<li>Look for the beautiful</li>\r\n<li>Bed time</li>\r\n<li>If I have a milion dollars</li>\r\n<li>Creative people</li>\r\n<li>Making cookie</li>\r\n<li>Thanks giving</li>\r\n<li>Radio stations</li>\r\n<li>A small town</li>\r\n</ol>\r\n<p><strong>Level 4:</strong></p>\r\n<ol>\r\n<li>FIFA</li>\r\n<li>The history of the English language</li>\r\n<li>Kings and&nbsp;Queens of England</li>\r\n<li>Violence on Television</li>\r\n<li>London</li>\r\n<li>Women in sport</li>\r\n</ol>', 1, 10, 0, 'Luyện nghe, đọc, viết tiếng Anh. Tiếng Anh song ngữ.', NULL, 'public', '2016-08-17 11:02:50', 'tieng-anh-hang-ngay', 1, 0, 'page', 'tieng-anh-hang-ngay1469798846.jpg', '', 0, '', ''),
(13, 'Tiếng Anh', '', 0, 0, 0, '', '', 'public', '2016-07-29 13:34:13', 'http://btit95.esy.es/tieng-anh-hang-ngay.html', 0, 0, 'navigation', '', '', 0, '', ''),
(14, 'Tiếng Anh hằng ngày - Level 1 - Lesson 1 - First snow fall', '<p style="text-align: left;"><strong>Nội dung của b&agrave;i:</strong></p>\r\n<p>First snow fall.<br />Tuyết rơi lần đầu.</p>\r\n<p>Today is Nov 26th.<br />H&ocirc;m nay l&agrave; ng&agrave;y 26 th&aacute;ng 11.</p>\r\n<p>It snowed all day today.<br />Tuyết đ&atilde; rơi cả ng&agrave;y h&ocirc;m nay.</p>\r\n<p>The snow is beautiful.<br />Tuyết thật l&agrave; đẹp.</p>\r\n<p>The snow finally stopped.<br />Cuối c&ugrave;ng th&igrave; tuyết cũng đ&atilde; ngừng rơi.</p>\r\n<p>My sister and I are excited.<br />Chị t&ocirc;i v&agrave; t&ocirc;i rất n&oacute;ng l&ograve;ng.</p>\r\n<p>My mom doesn''t like the snow.<br />Mẹ t&ocirc;i th&igrave; kh&ocirc;ng th&iacute;ch tuyết.</p>\r\n<p>My mom has to shovel the driveway.<br />Mẹ t&ocirc;i phải dọn đường đi.</p>\r\n<p>My sister and I get to play.<br />Chị t&ocirc;i v&agrave; t&ocirc;i được chơi đ&ugrave;a.</p>\r\n<p>I put on my hat and mittens.<br />T&ocirc;i đội mũ v&agrave; găng tay của m&igrave;nh.</p>\r\n<p>My mom puts on my scarf.<br />Mẹ t&ocirc;i qu&agrave;ng khăn của t&ocirc;i cho t&ocirc;i.</p>\r\n<p>My mom zippers my jacket.<br />Mẹ t&ocirc;i k&eacute;o kh&oacute;a &aacute;o kho&aacute;c của t&ocirc;i.</p>\r\n<p>My sister puts on her hat and mittens.<br />Chị t&ocirc;i đội mũ v&agrave; đeo găng tay của m&igrave;nh v&agrave;o.</p>\r\n<p>My mom puts on her scarf.<br />Mẹ t&ocirc;i qu&agrave;ng khăn của b&agrave; v&agrave;o.</p>\r\n<p>My mom zippers her jacket.<br />Mẹ t&ocirc;i k&eacute;o kh&oacute;a &aacute;o của m&igrave;nh.</p>\r\n<p>My sister and I go outside.<br />Chị t&ocirc;i v&agrave; t&ocirc;i đi ra ngo&agrave;i.</p>\r\n<p>We begin to make the snowman.<br />Ch&uacute;ng t&ocirc;i bắt đầu đắp&nbsp;người tuyết.</p>\r\n<p>My mom starts to shovel the snow.<br />Mẹ t&ocirc;i th&igrave; bắt đầu s&uacute;c tuyết.</p>\r\n<p>My sister and I make snow angles.<br />Chị t&ocirc;i v&agrave; t&ocirc;i đắp những thi&ecirc;n thần tuyết.</p>\r\n<p>My sister and I throw snowballs.<br />Chị t&ocirc;i v&agrave; t&ocirc;i nắm những cục tuyết.</p>\r\n<p>It starts to snow again.&nbsp;<br />Tuyết lại bắt đầu rơi trở lại.</p>\r\n<p>We go inside for hot chocolate.<br />Ch&uacute;ng t&ocirc;i đi v&agrave;o với m&oacute;n s&ocirc;-c&ocirc;-la n&oacute;ng.</p>\r\n<p style="text-align: center;"><span style="text-decoration: underline;"><em><a title="Tiếng anh hằng ng&agrave;y - phụ đề song ngữ" href="../../tieng-anh-hang-ngay.html">Trở về danh s&aacute;ch b&agrave;i học</a></em></span></p>', 1, 547, 0, 'Học tiếng Anh song ngữ: Trận mưa tuyết đầu tiên', NULL, 'public', '2016-08-13 06:08:16', 'tieng-anh-hang-ngay-level-1-lesson-1-first-snow-fall', 1, 0, 'post', 'tieng-anh-hang-ngay-level-1-lesson-1-first-snow-fall1469890457.jpg', '', 0, 'https://www.youtube.com/watch?v=BejQGsnFnKY', '[{"time":"0.33","en":"First snow fall","vi":"Tuyết rơi lần đầu"},{"time":"3.38","en":"Today is Nov 26th","vi":"Hôm nay là ngày 26 tháng 11"},{"time":"6.23","en":"It snowed all day today","vi":"Tuyết đã rơi cả ngày hôm nay"},{"time":"8.91","en":"The snow is beautiful","vi":"Tuyết thật là đẹp"},{"time":"11.6","en":"The snow finally stopped","vi":"Cuối cùng thì tuyết cũng đã ngừng rơi"},{"time":"14.9","en":"My sister and I are excited","vi":"Chị tôi và tôi rất nóng lòng"},{"time":"17.6","en":"My mom doesn''t like the snow","vi":"Mẹ tôi thì không thích tuyết"},{"time":"20.69","en":"My mom has to shovel the driveway","vi":"Mẹ tôi phải dọn đường đi"},{"time":"23.88","en":"My sister and I get to play","vi":"Chị tôi và tôi được chơi đùa"},{"time":"26.9","en":"I put on my hat and mittens","vi":"Tôi đội mũ và găng tay của mình"},{"time":"29.8","en":"My mom puts on my scarf","vi":"Mẹ tôi quàng khăn của tôi cho tôi"},{"time":"32.48","en":"My mom zippers my jacket","vi":"Mẹ tôi kéo khóa áo khoác của tôi"},{"time":"35.19","en":"My sister puts on her hat and mittens","vi":"Chị tôi đội mũ và đeo găng tay của mình vào"},{"time":"38.77","en":"My mom puts on her scarf","vi":"Mẹ tôi quàng khăn của bà vào"},{"time":"41.75","en":"My mom zippers her jacket","vi":"Mẹ tôi kéo khóa áo của mình"},{"time":"44.69","en":"My sister and I go outside","vi":"Chị tôi và tôi đi ra ngoài"},{"time":"47.8","en":"We begin to make the snowman","vi":"Chúng tôi bắt đầu đắp người tuyết"},{"time":"50.6","en":"My mom starts to shovel the snow","vi":"Mẹ tôi thì bắt đầu súc tuyết"},{"time":"53.72","en":"My sister and I make snow angles","vi":"Chị tôi và tôi đắp những thiên thần tuyết"},{"time":"56.8","en":"My sister and I throw snowballs","vi":"Chị tôi và tôi nắm những cục tuyết"},{"time":"59.8","en":"It starts to snow again","vi":"Tuyết lại bắt đầu rơi trở lại"},{"time":"62.3","en":"We go inside for hot chocolate","vi":"Chúng tôi đi vào với món sô-cô-la nóng"}]');
INSERT INTO `posts` (`p_id`, `p_title`, `p_content`, `p_author`, `p_view_count`, `p_comment_count`, `p_excerpt`, `p_catalogue`, `p_status`, `p_published`, `p_guid`, `p_comment_allow`, `p_menu_order`, `p_type`, `p_banner`, `p_password`, `p_parent`, `p_youtube_url`, `p_cue`) VALUES
(15, 'Lấy access token để truy vấn tới facebook', '<h5>Access token l&agrave; g&igrave;?</h5>\r\n<p>Để truy xuất kết quả từ FB bạn cần phải c&oacute; 1 access token, n&oacute; giống như một x&acirc;u ch&igrave;a kh&oacute;a để mở những rương đồ, mỗi rương đồ sẽ c&oacute; một ch&igrave;a kh&oacute;a ri&ecirc;ng. Điều đ&oacute; đồng nghĩa với việc nếu x&acirc;u ch&igrave;a kh&oacute;a của bạn c&oacute; nhiều ch&igrave;a th&igrave; bạn sẽ mở được nhiều rương v&agrave; lấy đi những thứ bạn muốn.</p>\r\n<h5 style="text-align: left;">L&agrave;m thế n&agrave;o để lấy được Access Token?</h5>\r\n<p>Một access token l&agrave;m một chuỗi string được FB m&atilde; h&oacute;a theo một c&aacute;ch thức n&agrave;o đ&oacute;, n&oacute; bao gồm 5 th&ocirc;ng tin ch&iacute;nh:</p>\r\n<ol>\r\n<li>Đại diện cho user n&agrave;o</li>\r\n<li>Được tạo bởi ứng dụng n&agrave;o</li>\r\n<li>Những quyền được ph&eacute;p</li>\r\n<li>C&aacute;c c&agrave;i đặt c&aacute; nh&acirc;n người d&ugrave;ng đ&atilde; chọn</li>\r\n<li>Thời hạn sử dụng</li>\r\n</ol>\r\n<p>C&oacute; 2 c&aacute;ch để lấy access token:</p>\r\n<ol>\r\n<li>Sử dụng tool của Facebook cung cấp<br />Để sử dụng tool n&agrave;y c&aacute;c bạn truy cập v&agrave;o địa chỉ:&nbsp;<a href="https://developers.facebook.com/tools/explorer/">https://developers.facebook.com/tools/explorer/</a>&nbsp;<br />Ở đ&acirc;y m&igrave;nh sẽ kh&ocirc;ng n&oacute;i nhiều về tool n&agrave;y n&ecirc;n c&aacute;c bạn c&oacute; thể tham khảo th&ecirc;m ở &nbsp;<a href="https://developers.facebook.com/docs/graph-api">https://developers.facebook.com/docs/graph-api</a><br /><br /></li>\r\n<li>Sử dụng chức năng login<br />M&igrave;nh sẽ sử dụng chức năng login đ&atilde; l&agrave;m ở <span style="text-decoration: underline;"><strong><em><a title="Tạo chức năng login với facebook" href="../../tao-chuc-nang-login-facebook-don-gian-voi-javascript-11.html">b&agrave;i trước</a></em></strong></span> để lấy access token, n&ecirc;n nếu bạn chưa biết thực hiện chức năng n&agrave;y c&oacute; thể trở lại để tham khảo.</li>\r\n</ol>\r\n<h5>Lấy access token từ chức năng login</h5>\r\n<p>Trong m&atilde; nguồn của b&agrave;i trước, bạn h&atilde;y edit phương thức RequestLoginFB một ch&uacute;t:</p>\r\n<pre class="language-javascript"><code>// Gửi y&ecirc;u cầu đăng nhập tới FB\r\nfunction RequestLoginFB() {\r\n    window.location = ''http://graph.facebook.com/oauth/authorize?'' \r\n    + ''client_id=1567628656880346'' + // Đổi App ID của bạn ở đ&acirc;y\r\n ''&amp;scopes=public_profile,email,user_likes,user_birthday,'' \r\n+ ''ser_education_history,user_work_history,user_posts, user_photos,'' \r\n+ ''user_location, publish_actions&amp;redirect_uri=http://btit95.com/fb/index.html'';\r\n}</code></pre>\r\n<p>Sau đ&oacute; ch&uacute;ng ta truy cập&nbsp;lại v&agrave;o <a href="http://btit95.com/fb/">http://btit95.com/fb/</a>&nbsp;nếu kh&ocirc;ng thấy button Login with Facebook th&igrave; c&oacute; nghĩa l&agrave; bạn đ&atilde; đăng nhập ở phi&ecirc;n trước, bạn cần logout khỏi app v&agrave; đăng nhập lại để thấy sự thay đổi.<br />H&atilde;y truy cập v&agrave;o facebook sau đ&oacute; v&agrave;o phần setting -&gt; Apps v&agrave; remove App m&agrave; bạn đ&atilde; login đi.</p>\r\n<p style="text-align: center;"><img class="lazy zoomable" alt="Đăng xuất khỏi app" width="500" height="258" data-original="../../source/logout%20app.png?1469872285997" /></p>\r\n<p style="text-align: left;">Sau khi reomve bạn truy cập lại <a href="http://btit95.com/fb/">http://btit95.com/fb/</a>&nbsp;sẽ xuất hiện button Login with Facebook. Khi đ&oacute; tiến h&agrave;nh đăng nhập lại sẽ thu được kết quả như sau:</p>\r\n<p style="text-align: left;"><br /><img class="lazy zoomable" style="display: block; margin-left: auto; margin-right: auto;" alt="Login facebook để lấy access token" width="500" height="281" data-original="../../source/login%20success%20with%20token.png?1469872796660" /></p>\r\n<p style="text-align: left;">Kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c ngo&agrave;i bạn sẽ nhận được một chuỗi token tr&ecirc;n url, chuỗi n&agrave;y k&eacute;o d&agrave;i từ dấu "=" cho tới dấu "&amp;", h&atilde;y lấy chuỗi đ&oacute; v&agrave; lưu v&agrave;o nodepad. Như vậy ch&uacute;ng ta đ&atilde; lấy được access token th&ocirc;ng qua chức năng login. B&agrave;i sau sẽ sử dụng token n&agrave;y để lấy th&ocirc;ng tin từ FB.</p>', 1, 269, 0, 'Để truy xuất kết quả từ FB bạn cần phải có 1 access token, nó giống như một xâu chìa khóa để mở những rương đồ, mỗi rương đồ sẽ có một chìa khóa riêng. Điều đó đồng nghĩa với việc nếu xâu chìa khóa của bạn có nhiều chìa thì bạn sẽ mở được nhiều rương và...', NULL, 'public', '2016-07-30 11:03:15', 'lay-access-token-de-truy-van-toi-facebook', 1, 0, 'post', 'lay-access-token-de-truy-van-toi-facebook1469874942.jpg', '', 0, '', ''),
(16, 'Sử dụng Facebook API', '<p>Đ&ocirc;i khi ch&uacute;ng ta thắc mắc tại sao một số ứng dụng thường thấy tr&ecirc;n Facebook như Người y&ecirc;u tương lai của bạn l&agrave; ai? Avatar facebook của bạn n&oacute;i l&ecirc;n điều g&igrave;? T&ecirc;n facebook của bạn c&oacute; &yacute; nghĩa như thế n&agrave;o? Lại c&oacute; được những th&ocirc;ng tin h&igrave;nh ảnh, hay danh s&aacute;ch bạn b&egrave; của ch&uacute;ng ta để họ viết app mặc d&ugrave; app của họ kh&ocirc;ng thuộc về facebook.</p>\r\n<p>Nếu để &yacute; khi ch&uacute;ng ta tham gia những app như thế th&igrave; app sẽ y&ecirc;u cầu bạn phải đăng nhập v&agrave; bạn phải cho ph&eacute;p một số quyền truy cập t&agrave;i nguy&ecirc;n tr&ecirc;n t&agrave;i khoản facebook của bạn như: xem public profile, photo albums, list friends,...</p>\r\n<p style="text-align: center;"><img class="lazy zoomable wp-caption" alt="Y&ecirc;u cầu login trước khi sử dụng app" width="500" height="212" data-original="../../source/login%20before%20play%20app.jpg?1469864644658" /><br />Y&ecirc;u cầu đăng nhập v&agrave;o Facebook</p>\r\n<p style="text-align: center;"><img class="lazy zoomable wp-caption" alt="Y&ecirc;u cầu quyền truy cập t&agrave;i nguy&ecirc;n tr&ecirc;n t&agrave;i khoản facebook" width="500" height="227" data-original="../../source/permission%20required.jpg?1469864771878" /><br />Y&ecirc;u cầu quyền truy cập t&agrave;i nguy&ecirc;n tr&ecirc;n t&agrave;i khoản</p>\r\n<p style="text-align: left;">Khi m&igrave;nh&nbsp;đăng nhập v&agrave; chấp nhận cho app nhận c&aacute;c th&ocirc;ng tin m&agrave; n&oacute; y&ecirc;u cầu th&igrave; n&oacute; sẽ bắt đầu xử l&yacute; th&ocirc;ng tin v&agrave; trả về kết quả cho ch&uacute;ng ta. Vậy l&agrave;m thế n&agrave;o để l&agrave;m được điều n&agrave;y? <strong>Facebook hỗ trợ API</strong> cho ch&uacute;ng ta l&agrave;m những việc đ&oacute; <strong>với sự chấp nhận của người d&ugrave;ng</strong>.</p>\r\n<h5 style="text-align: left;"><strong>Facebook API l&agrave; g&igrave;?</strong></h5>\r\n<p>Facebook API l&agrave; phương ph&aacute;p gi&uacute;p lập tr&igrave;nh vi&ecirc;n c&oacute; thể tạo ra c&aacute;c app b&ecirc;n ngo&agrave;i tương t&aacute;c với cơ sở dữ liệu của FB theo <strong>giao thức http</strong> v&agrave; theo 2 phương thức l&agrave; GET v&agrave; POST tr&ecirc;n cổng giao tiếp m&agrave; FB hỗ trợ.</p>\r\n<h5>Loạt b&agrave;i trong series Sử dụng Facebook API:</h5>\r\n<ol>\r\n<li><span style="text-decoration: underline;"><em><strong><a title="Tạo facebook application" href="../../tao-facebook-application-9.html">C&aacute;ch tạo&nbsp;một facebook application</a></strong></em></span></li>\r\n<li><span style="text-decoration: underline;"><em><strong><a title="C&agrave;i đặt facebook app để test ở localhost" href="../../setting-facebook-app-de-test-o-localhost-10.html">C&agrave;i đặt facebook app để test ở localhost</a></strong></em></span></li>\r\n<li><span style="text-decoration: underline;"><strong><em><a title="Tạo chức năng login với Facebook sử dụng javascript" href="../../tao-chuc-nang-login-facebook-don-gian-voi-javascript-11.html">Tạo chức năng login với Facebook sử dụng javascript</a></em></strong></span></li>\r\n<li><span style="text-decoration: underline;"><em><strong><a title="Lấy access token để gửi truy vấn tới facebook" href="../../lay-access-token-de-truy-van-toi-facebook-15.html">Lấy access token để gửi truy vấn tới facebook</a></strong></em></span></li>\r\n<li>Sử dụng GET method để lấy th&ocirc;ng tin từ facebook</li>\r\n<li>Tương t&aacute;c với facebook sử dụng POST method</li>\r\n<li>Truy vấn n&acirc;ng cao</li>\r\n</ol>', 1, 37, 0, 'Đôi khi chúng ta thắc mắc tại sao một số ứng dụng thường thấy trên Facebook như Người yêu tương lai của bạn là ai? Avatar facebook của bạn nói lên điều gì? Tên facebook của bạn có ý nghĩa như thế nào? Lại có được những thông tin hình ảnh, hay danh sách bạn bè của chúng ta để họ viết app mặc dù app của họ không thuộc về facebook.', NULL, 'public', '2016-08-11 14:08:13', 'su-dung-facebook-api', 1, 0, 'page', 'su-dung-facebook-api1469874254.jpg', '', 0, '', ''),
